    <style>
        @import url('https://fonts.googleapis.com/css2?family=Bebas+Neue&family=Noto+Sans:wght@400;700&display=swap');

*{
    margin: 0;
    padding: 0;
    font-family: 'Bebas Neue',Arial, Helvetica, sans-serif;
    box-sizing: border-box;
}

body{
    margin: 0;
    font-family: Arial;
    background-color: #292929;
    color:aliceblue;
    padding: 0;
}

header{
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-family: Arial, Helvetica, sans-serif;
    padding: 0px;
    background-color: #D1E0EE;
    margin: 0;
    height: 11.3vh;
    box-shadow: -3px 2px 15px 1px #000000b5;
}

.form {
    font-family: 'Noto Sans', sans-serif;
    color: #f0f8ff;
    display: flex;
    flex-direction: column;
    align-items: center;
}

/* Estilizando os botões */
.botoes{
    border: 1px solid beige;
    width: 35vw;
    height: 45vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin: 10px 0px;
    /* background: #1a1820; */
    padding: 15px;
    border-radius: 18px;
    margin: 8vh;
}

.botoes > input{ 
    border: none;
    border-radius: 10px;
    padding: 8px;
    background: #004788;
    color: #f0f8ffe3;
    font-size: 12pt;
    box-shadow: 0px 10px 5px #00000012;
    outline: none;
    
}

.textfield{
    border: 10px;
}

.textfield > input{
    width: 96%;
    border: none;
    border-radius: 10px;
    padding: 6px;
    color: black;
    outline: none;
    font-size: 12pt;
    box-sizing: border-box;
    margin-bottom: 5px;
}

.barra  {
    border: 1px solid black;
    border-radius: 1rem;
    padding-right: 0.5rem;
    padding-top: 0.1rem;
}  

button, input, optgroup, select, textarea {
    margin: 0;
    font-family: inherit;
    font-size: inherit;
    line-height: inherit;
}

.logo{
    width: 5vw;
}

.carrinho{
    width: 3.6vw;
    right: 1vw;
    top: 2vh;
}

.lupa{
    width: 1.6rem;
}

input:focus{
    outline: none;
}

header > div > input{
    text-decoration: none;
    margin: 0;
    border-radius: 18px;
    padding: 0.2rem 1rem;
    width: 50vh;
    background: none;
    text-decoration-style: none;
    border: 0;
}

.btn-login{
    margin-top: 10px;
    width: 98%;
    padding: 16px 0px;
    border: none;
    border-radius: 8px;
    outline: none;
    text-transform: uppercase;
    font-weight: 800;
    letter-spacing: 2px;
    background-color: #62aaed;
    cursor: pointer;
    margin-top: 15px;
}

main, header, nav, footer{
    width: 100%;
}

form{
    display: flex;
    justify-content: center;
}

footer{
    background-color: #292929;
    color: aliceblue;
    height: 24vh;
}

footer,div.texto{
    height: 100%;
    flex-direction: column;
    display: flex;
}

div > svg{
    padding: 0 0.2vw;
}

.insta{
    text-align: center;
}

.insta > a{
    text-decoration: none;
    color: white;
}

.imagens{
    padding: 0px;
    text-align: center;
    /* display: flex; */
    /* max-width: 20vw; */
    max-height: -1vh;
    /* align-items: start;*/
}

.imagens, .imagens1,.imagens2{
    justify-content: center;
}

.imagens1{
    display: flex;
    flex-direction: row-reverse;
}

.imagens2{
    display: flex;
    flex-direction: row;
}

div.imagens > a{
    color: white;
    text-decoration: none;
    text-decoration-style: none;
}

div.texto{
    font-size: 1.1em;
    text-decoration: none;
    display: flex;
    padding: 15px;
    flex-direction: row;
    justify-content: space-between;
}

div.texto > p > a{
    
    color: white;
    text-decoration: none;
    text-decoration-style: none;
}

#lateral{
    align-items:center;
    justify-content: center;
    background-color: #D1E0EE;
    position: fixed;
    right: 0;
    text-decoration: none;
    z-index: 1;
    font-size: 1.1em;
    list-style: none;
    width: 0px;
    transition: width 0.3s;
    transition: all 0.4s;
    z-index: 2;
}

#lateral a{
    padding: 5px;
    display: flex;
    flex-direction: column;
    text-decoration: none;
    color: #292929;
    margin: 10px;
    align-items: center;
    overflow: hidden;
}

#lateral a:hover{
    color: white;
    background-color: #292929;
}

/*imagem*/
.instagram{
    width: 12%;
}

@media screen and (max-width: 650px) {
    
    .logo, .carrinho{
        width: 3rem;
        
    }

    .botoes{
        width: 90%;
        height: auto;
        align-items: center;
        justify-content: center;
    }
    
    .lupa{
        display: none;
    }

    .barra{
        display: none;
    }

    footer,div.texto, footer > div > p > a{
        padding: 25px;
        align-items: center;
        text-align: center;
        height: 100%;
        flex-direction: column;
        display: flex;
    }

    div.imagens{
        display: none;
    }
}
